mapper.update:
update sboard2
set BTITLE=#{btitle}, BCONTENT=#{bcontent}, BFILE=#{bfile, jdbcType=VARCHAR}
where ID=#{id} and BPASS=#{bpass}

update sboard2
set bhit = bhit + 1
where id=#{id}

mapper.select:
select * from(
	select row_number() over(order by id desc) as rnum, 
		id, app_user_id, btitle, bcontent, bpass, bfile, bip, bhit, created_at
	from sboard2
) A
where A.rnum between #{start} and #{end}
